import React, { useEffect, useState } from "react";
// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import { makeStyles } from "@material-ui/core/styles";
import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
import BugReport from "@material-ui/icons/BugReport";
import Code from "@material-ui/icons/Code";
import Cloud from "@material-ui/icons/Cloud";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Tasks from "components/Tasks/Tasks.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";

import { bugs, website, server } from "variables/general.js";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import Typography from "@material-ui/core/Typography";

import axios from "axios";

import {
  dailySalesChart,
  emailsSubscriptionChart,
  completedTasksChart,
} from "variables/charts.js";

import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { object } from "prop-types";

const useStyles = makeStyles(styles);

export default function Dashboard() {
  const classes = useStyles();
  let [todos, updateTodo] = useState([]);
  const UserEmail = "pravin@pravin.com";
  // const UserEmail = props.user.email;
  let toDoList = [];
  let getData = async () => {
    let getService = await axios.get("http://localhost:3001/getService");
    let todoResponse = await axios.post("http://localhost:3001/getList", {
      UserEmail,
    });
    // console.log("pravin", todoResponse, { getService });

    toDoList = todoResponse.data.map((value, keys) => value.description);
    console.log("pravin toDoList---", toDoList.length);
    updateTodo(todoResponse.data);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div>
      <GridContainer>
        <GridItem xs={12} sm={12} md={4}>
          <Card chart>
            <CardHeader color="success">
              <ChartistGraph
                className="ct-chart"
                data={dailySalesChart.data}
                type="Line"
                options={dailySalesChart.options}
                listener={dailySalesChart.animation}
              />
            </CardHeader>
            <CardBody>
              <h4 className={classes.cardTitle}>Daily Sales</h4>
              <p className={classes.cardCategory}>
                <span className={classes.successText}>
                  <ArrowUpward className={classes.upArrowCardCategory} /> 55%
                </span>{" "}
                increase in today sales.
              </p>
            </CardBody>
            <CardFooter chart>
              <div className={classes.stats}>
                <AccessTime /> updated 4 minutes ago
              </div>
            </CardFooter>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={4}>
          <Card chart>
            <CardHeader color="warning">
              <ChartistGraph
                className="ct-chart"
                data={emailsSubscriptionChart.data}
                type="Bar"
                options={emailsSubscriptionChart.options}
                responsiveOptions={emailsSubscriptionChart.responsiveOptions}
                listener={emailsSubscriptionChart.animation}
              />
            </CardHeader>
            <CardBody>
              <h4 className={classes.cardTitle}>Email Subscriptions</h4>
              <p className={classes.cardCategory}>Last Campaign Performance</p>
            </CardBody>
            <CardFooter chart>
              <div className={classes.stats}>
                <AccessTime /> campaign sent 2 days ago
              </div>
            </CardFooter>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={4}>
          <Card chart>
            <CardHeader color="danger">
              <ChartistGraph
                className="ct-chart"
                data={completedTasksChart.data}
                type="Line"
                options={completedTasksChart.options}
                listener={completedTasksChart.animation}
              />
            </CardHeader>
            <CardBody>
              <h4 className={classes.cardTitle}>Completed Tasks</h4>
              <p className={classes.cardCategory}>Last Campaign Performance</p>
            </CardBody>
            <CardFooter chart>
              <div className={classes.stats}>
                <AccessTime /> campaign sent 2 days ago
              </div>
            </CardFooter>
          </Card>
        </GridItem>
      </GridContainer>
      <GridContainer>
        <GridItem xs={12} sm={12} md={6}>
          {toDoList.length > 0 && (
            <CustomTabs
              title="Tasks:"
              headerColor="primary"
              tabs={[
                {
                  tabName: "Bugs",
                  tabIcon: BugReport,
                  tabContent: (
                    <Tasks
                      checkedIndexes={[0, 3]}
                      tasksIndexes={[0, 1, 2, 3, 4]}
                      tasks={toDoList}
                    />
                  ),
                },
                {
                  tabName: "Website",
                  tabIcon: Code,
                  tabContent: (
                    <Tasks
                      checkedIndexes={[0]}
                      tasksIndexes={[0, 1]}
                      tasks={website}
                    />
                  ),
                },
                {
                  tabName: "Server",
                  tabIcon: Cloud,
                  tabContent: (
                    <Tasks
                      checkedIndexes={[1]}
                      tasksIndexes={[0, 1, 2]}
                      tasks={server}
                    />
                  ),
                },
              ]}
            />
          )}
          <GridContainer item xs={12} md={4}>
            <List className={classes.root}>
              {todos.length > 0 &&
                todos.map((val) => (
                  <ListItem key={val.todo_id} role={undefined} dense button>
                    <ListItemIcon>
                      <Checkbox
                        onChange={(event) =>
                          handleUpdate("status", { id: val.todo_id, event })
                        }
                        edge="start"
                        checked={val.status === 1 ? true : false}
                        disableRipple
                        inputProps={{ "aria-labelledby": val.todo_id }}
                      />
                    </ListItemIcon>
                    <ListItemText
                      id={val.todo_id}
                      primary={
                        val.status === 1 ? (
                          <Typography type="body2" style={textStyle}>
                            {" "}
                            {`${val.title} - ${val.date.split("T")[0]}`}
                          </Typography>
                        ) : (
                          <Typography type="body2">
                            {`${val.title} - ${val.date.split("T")[0]}`}{" "}
                          </Typography>
                        )
                      }
                      secondary={
                        val.status === 1 ? (
                          <Typography style={textStyle}>
                            {" "}
                            {val.description}{" "}
                          </Typography>
                        ) : (
                          <Typography> {val.description} </Typography>
                        )
                      }
                    />

                    <ListItemSecondaryAction>
                      <IconButton
                        onClick={() => editList(val)}
                        color="secondary"
                        aria-label="comments"
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        onClick={() => handleUpdate("delete", val.todo_id)}
                        edge="end"
                        aria-label="comments"
                      >
                        <DeleteIcon />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
            </List>
          </GridContainer>
        </GridItem>
      </GridContainer>
    </div>
  );
}
